CREATE TABLE "public"."content_master" ("id" bigserial NOT NULL, "file_name" text NOT NULL, "link" text NOT NULL, "upload_date" date NOT NULL, "uploaded_by" text NOT NULL, "associated_event_id" bigint NOT NULL, "associated_group_id" bigint NOT NULL, "category_id" bigint NOT NULL, "type" text NOT NULL, "published_by" text NOT NULL, "publishing_date" date NOT NULL, "expiry_date" date NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("associated_event_id") REFERENCES "public"."event_master"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("associated_group_id") REFERENCES "public"."group_master"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("category_id") REFERENCES "public"."category_master"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("id"));
